import React from 'react';
import ReactDOM from 'react-dom';
import restaurants from '../sample-restaurants';

class Landing extends React.Component {

    state = {

        display: false,
        title: '',
        url: '',


    };

    displayList = () => {

        const {
            display
        } = this.state;
        this.setState = ({
            display: !display
        });
    };

    getTitle = (r) => {

        const {
            title, url
        } = this.state;
        this.setState = ({
            title, url, display: false
        })
    };




    render() {
        return ( < div className = "restaurant_select" > < div className = "restaurant_select_top" > < div onClick = {
                this.displayList
            }
            className = "restaurant_select_top_header font-effect-outline " > {
                this.state.title ? this.state.title : 'Выберите Ресторан'
            } < /div>

		<div className="arrow_picker">
		 <div className="arrow_picker-up"></div > < div className = "arrow_picker-down" > < /div>
	</div > < /div>

	{this.state.display? (
	<div className="restaurant_select_burger">
	 <ul>
	   {restaurants.map( r=>{
        return <li onClick={ () => this.getTitle(r)} key={r.id}> {r.title} </li >
        })
} < /ul>

	</div >
    ): null
} < button > Перейтивресторан < /button>

	</div >

)
}
}


export
default Landing;
